import firebase from 'firebase';

export default {
  name: 'login',
  data: function() {
    return {
      email: '',
      emailRules: [
      v => !!v || 'E-mail is required',
      v => /.+@.+/.test(v) || 'Email inválido'
    ],
      password: ''
    };
  },
  methods: {
    login: function(e) {
      firebase
        .auth()
        .signInWithEmailAndPassword(this.email, this.password)
        .then(
          user => {
            //alert(`You are logged in as ${user.email}`);
            this.$router.push({ path: '/' });
          },
          err => {
            alert(err.message);
          }
        );
      e.preventDefault();
    }
  }
};
